#include <string.h>
#include "stm32f446xx.h"

char packets_keys[] = {'V','C','F','D','Z','S','X','N'};
uint32_t packets_value[5];

uint8_t status = 0b00000011;
uint8_t frequency = 96;

uint8_t heartbeat[5];
uint8_t telemetry[35];

uint8_t comm[3];

uint8_t dma_ready;
uint8_t telemetry_status;
uint8_t heartbeat_status;

char receive_data[1000];

void executeCommand(uint8_t command)
{
	status = command;
}

uint8_t validatePacket(char s)
{
	uint8_t x;
	for(x = 0; packets_keys[x] != s; x++);
	return packets_keys[x];
}

uint32_t getValue_Variable(char s)
{
	uint8_t x;
	for(x = 0; packets_keys[x] != s; x++);
	return packets_value[x];
}

void addValue_Variable(char s, uint8_t message[7])
{
	int x;
	for(x = 0; packets_keys[x] != s; x++);
	for(int y = 2; y < 6; y++)
	{
		packets_value[x] |= (message[x] << (y-2)*8) & 0xFF;
	}
}

void heartbeatStructure(void)
{
	heartbeat[0] = '$';
	heartbeat[1] = 'S';
	heartbeat[2] = 2;
}

